package com.example.ktoe.models;import java.time.LocalDateTime;import java.util.Objects;public class Paragraph {    private int paragraphId;    private int contentId;    private int paragraphOrder;    private String koreanParagraph;    private String englishParagraph;    private LocalDateTime createdAt;    private LocalDateTime updatedAt;    public Paragraph() {    }    public Paragraph(int paragraphId, int contentId, int paragraphOrder, String koreanParagraph, String englishParagraph, LocalDateTime createdAt, LocalDateTime updatedAt) {        this.paragraphId = paragraphId;        this.contentId = contentId;        this.paragraphOrder = paragraphOrder;        this.koreanParagraph = koreanParagraph;        this.englishParagraph = englishParagraph;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }    public int getParagraphId() {        return paragraphId;    }    public void setParagraphId(int paragraphId) {        this.paragraphId = paragraphId;    }    public int getContentId() {        return contentId;    }    public void setContentId(int contentId) {        this.contentId = contentId;    }    public int getParagraphOrder() {        return paragraphOrder;    }    public void setParagraphOrder(int paragraphOrder) {        this.paragraphOrder = paragraphOrder;    }    public String getKoreanParagraph() {        return koreanParagraph;    }    public void setKoreanParagraph(String koreanParagraph) {        this.koreanParagraph = koreanParagraph;    }    public String getEnglishParagraph() {        return englishParagraph;    }    public void setEnglishParagraph(String englishParagraph) {        this.englishParagraph = englishParagraph;    }    public LocalDateTime getCreatedAt() {        return createdAt;    }    public void setCreatedAt(LocalDateTime createdAt) {        this.createdAt = createdAt;    }    public LocalDateTime getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(LocalDateTime updatedAt) {        this.updatedAt = updatedAt;    }    @Override    public String toString() {        return "Paragraph{" +            "paragraphId=" + paragraphId +            ", contentId=" + contentId +            ", paragraphOrder=" + paragraphOrder +            ", koreanParagraph='" + koreanParagraph + '\'' +            ", englishParagraph='" + englishParagraph + '\'' +            ", createdAt=" + createdAt +            ", updatedAt=" + updatedAt +            '}';    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Paragraph paragraph = (Paragraph) o;        return paragraphId == paragraph.paragraphId &&            contentId == paragraph.contentId &&            paragraphOrder == paragraph.paragraphOrder &&            Objects.equals(koreanParagraph, paragraph.koreanParagraph) &&            Objects.equals(englishParagraph, paragraph.englishParagraph) &&            Objects.equals(createdAt, paragraph.createdAt) &&            Objects.equals(updatedAt, paragraph.updatedAt);    }    @Override    public int hashCode() {        return Objects.hash(paragraphId, contentId, paragraphOrder, koreanParagraph, englishParagraph, createdAt, updatedAt);    }}