package com.example.ktoe.models;import java.time.LocalDateTime;import java.util.Objects;public class Sentence {    private int sentenceId;    private int paragraphId;    private int sentenceOrder;    private String koreanSentence;    private String englishSentence;    private LocalDateTime createdAt;    private LocalDateTime updatedAt;    public Sentence() {    }    public Sentence(int sentenceId, int paragraphId, int sentenceOrder, String koreanSentence, String englishSentence, LocalDateTime createdAt, LocalDateTime updatedAt) {        this.sentenceId = sentenceId;        this.paragraphId = paragraphId;        this.sentenceOrder = sentenceOrder;        this.koreanSentence = koreanSentence;        this.englishSentence = englishSentence;        this.createdAt = createdAt;        this.updatedAt = updatedAt;    }    public int getSentenceId() {        return sentenceId;    }    public void setSentenceId(int sentenceId) {        this.sentenceId = sentenceId;    }    public int getParagraphId() {        return paragraphId;    }    public void setParagraphId(int paragraphId) {        this.paragraphId = paragraphId;    }    public int getSentenceOrder() {        return sentenceOrder;    }    public void setSentenceOrder(int sentenceOrder) {        this.sentenceOrder = sentenceOrder;    }    public String getKoreanSentence() {        return koreanSentence;    }    public void setKoreanSentence(String koreanSentence) {        this.koreanSentence = koreanSentence;    }    public String getEnglishSentence() {        return englishSentence;    }    public void setEnglishSentence(String englishSentence) {        this.englishSentence = englishSentence;    }    public LocalDateTime getCreatedAt() {        return createdAt;    }    public void setCreatedAt(LocalDateTime createdAt) {        this.createdAt = createdAt;    }    public LocalDateTime getUpdatedAt() {        return updatedAt;    }    public void setUpdatedAt(LocalDateTime updatedAt) {        this.updatedAt = updatedAt;    }    @Override    public String toString() {        return "Sentence{" +            "sentenceId=" + sentenceId +            ", paragraphId=" + paragraphId +            ", sentenceOrder=" + sentenceOrder +            ", koreanSentence='" + koreanSentence + '\'' +            ", englishSentence='" + englishSentence + '\'' +            ", createdAt=" + createdAt +            ", updatedAt=" + updatedAt +            '}';    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Sentence sentence = (Sentence) o;        return sentenceId == sentence.sentenceId &&            paragraphId == sentence.paragraphId &&            sentenceOrder == sentence.sentenceOrder &&            Objects.equals(koreanSentence, sentence.koreanSentence) &&            Objects.equals(englishSentence, sentence.englishSentence) &&            Objects.equals(createdAt, sentence.createdAt) &&            Objects.equals(updatedAt, sentence.updatedAt);    }    @Override    public int hashCode() {        return Objects.hash(sentenceId, paragraphId, sentenceOrder, koreanSentence, englishSentence, createdAt, updatedAt);    }}