package com.example.sessionlogin.controllers;import jakarta.servlet.http.HttpSession;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;@Controllerpublic class ViewController {    @GetMapping("/")    public String home(Model model) {        model.addAttribute("title", "Home Page");        model.addAttribute("content", "home.jsp");        return "layout";    }    @GetMapping("/main")    public String main(HttpSession session, Model model) {        model.addAttribute("title", "Main Page");        model.addAttribute("content", "main.jsp");        return "layout";    }    @GetMapping("/about")    public String about(Model model) {        model.addAttribute("title", "About Us");        model.addAttribute("content", "about.jsp");        return "layout";    }}