package com.example.sessionlogin.services;import com.example.sessionlogin.mappers.MemberMapper;import com.example.sessionlogin.models.Member;import org.springframework.stereotype.Service;import org.slf4j.Logger;import org.slf4j.LoggerFactory;@Servicepublic class MemberService {    private static final Logger logger = LoggerFactory.getLogger(MemberService.class);    private final MemberMapper memberMapper;    public MemberService(MemberMapper memberMapper) {        this.memberMapper = memberMapper;    }    public boolean createMember(Member member) {        logger.info("createMember member: {}", member.toString());        return memberMapper.insertMember(member) > 0;    }}